package datatypes;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	    System.out.println("Hello World!");
		
		
		
		// -128 to 127
		byte enKucukSayi = 127;
		System.out.println(enKucukSayi);
		
		// -32768 to 32767
		short kucukSayi = 3568;
		System.out.println(kucukSayi);
		
		// -2147483648 to 2147483647
		int sayi = 1545468458;
		System.out.println(sayi);
		
	    double ondalik = 12.5;
	    System.out.println(ondalik);
	    
	    // tek karakter için ve tek tırnak ile
	    char karakter = 'A';
	    System.out.println(karakter);
	    
	    // doğru ya da yanlış
	    boolean dogruMu = false;
	    System.out.println(dogruMu);
	
	
	
	
	
	
	}

}
